import React, { useEffect, useRef } from "react";
import "./LandingPage.css";

const LandingPage = () => {
  const paperRef = useRef();

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      // Rotate up to 30deg as you scroll 0-500px
      const rotate = Math.min(30, scrollY / 16);
      if (paperRef.current) {
        paperRef.current.style.transform = `perspective(1200px) rotateY(${rotate}deg)`;
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="landing-bg">
      <div className="landing-3d-wrapper">
        <div className="landing-3d-paper" ref={paperRef}>
          <h1 className="landing-title">Welcome to Resume Analyzer</h1>
          <p className="landing-sub">
            AI-powered, ATS-friendly, and beautifully designed. Analyze your
            resume and get hired faster!
          </p>
          <a href="#app" className="landing-cta">
            Get Started
          </a>
        </div>
      </div>
      <div className="landing-scroll-indicator">
        <span className="scroll-arrow">â†“</span>
        <span className="scroll-text">Scroll Down</span>
      </div>
    </div>
  );
};

export default LandingPage;
