// frontend/src/components/ResumeDetails.js
import React from "react";

// --- HELPER COMPONENTS for a cleaner, more modular UI ---

// A Badge/Pill for skills and technologies (creative)
const Badge = ({ text }) => (
  <span className="badge creative-badge">
    <span role="img" aria-label="star" className="badge-icon">
      ⭐
    </span>{" "}
    {text}
  </span>
);

// A Card component for consistent styling of sections (creative)
const Card = ({ children, className = "" }) => (
  <div className={`card creative-card ${className}`}>{children}</div>
);

// An Accordion for the AI analysis sections to keep the UI tidy (creative)
const Accordion = ({ title, items }) => {
  if (!Array.isArray(items) || items.length === 0) return null;
  return (
    <details className="accordion-item creative-accordion">
      <summary className="accordion-title creative-accordion-title">
        {title}
      </summary>
      <ul className="accordion-content creative-accordion-content">
        {items.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </details>
  );
};

// --- MAIN COMPONENT ---

const ResumeDetails = ({ data }) => {
  if (!data) {
    return (
      <div className="loading-container creative-loading">
        <p>No resume data to display.</p>
      </div>
    );
  }

  const {
    contact_info,
    summary_objective,
    work_experience,
    education,
    skills,
    projects,
    awards_certifications,
    resume_analysis,
  } = data;

  return (
    <div className="resume-container creative-resume-container">
      {/* == Main Content Column == */}
      <main className="resume-main creative-resume-main">
        {/* Header Section */}
        <header className="resume-header creative-resume-header">
          <h1>
            <span role="img" aria-label="user">
              👤
            </span>{" "}
            {contact_info?.name || "Candidate Name"}
          </h1>
          <div className="contact-links creative-contact-links">
            {contact_info?.email && (
              <span title="Email">📧 {contact_info.email}</span>
            )}
            {contact_info?.phone && (
              <span title="Phone">📞 {contact_info.phone}</span>
            )}
            {contact_info?.linkedin && (
              <span title="LinkedIn">
                🔗{" "}
                <a
                  href={contact_info.linkedin}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  LinkedIn
                </a>
              </span>
            )}
            {contact_info?.github && (
              <span title="GitHub">
                💻{" "}
                <a
                  href={contact_info.github}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  GitHub
                </a>
              </span>
            )}
          </div>
        </header>

        {/* Summary Section */}
        {summary_objective && (
          <Card>
            <h2>🎯 Objective</h2>
            <p>{summary_objective}</p>
          </Card>
        )}

        {/* Work Experience Section (Timeline) */}
        {work_experience?.length > 0 && (
          <section>
            <h2>💼 Work Experience</h2>
            <div className="timeline creative-timeline">
              {work_experience.map((job, index) => (
                <div
                  key={index}
                  className="timeline-item creative-timeline-item"
                >
                  <div className="timeline-dot creative-timeline-dot"></div>
                  <div className="timeline-content creative-timeline-content">
                    <h3>{job.title || "Job Title"}</h3>
                    <p className="timeline-subtitle creative-timeline-subtitle">
                      {job.company || "Company"} | {job.dates || "Dates"}
                    </p>
                    <p className="timeline-location creative-timeline-location">
                      {job.location || "Location"}
                    </p>
                    <ul>
                      {job.description_points?.map((point, i) => (
                        <li key={i}>{point}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}

        {/* Projects Section */}
        {projects?.length > 0 && (
          <section>
            <h2>🛠️ Projects</h2>
            <div className="projects-grid creative-projects-grid">
              {projects.map((project, index) => (
                <Card
                  key={index}
                  className="project-card creative-project-card"
                >
                  <h3>{project.name || "Project Name"}</h3>
                  <p>{project.description || "No description provided."}</p>
                  {project.technologies?.length > 0 && (
                    <div className="tech-tags creative-tech-tags">
                      <strong>Technologies:</strong>
                      <div className="badge-container creative-badge-container">
                        {project.technologies.map((tech) => (
                          <Badge key={tech} text={tech} />
                        ))}
                      </div>
                    </div>
                  )}
                </Card>
              ))}
            </div>
          </section>
        )}

        {/* Education Section */}
        {education?.length > 0 && (
          <section>
            <h2>🎓 Education</h2>
            <div className="timeline creative-timeline">
              {education.map((edu, index) => (
                <div
                  key={index}
                  className="timeline-item creative-timeline-item"
                >
                  <div className="timeline-dot creative-timeline-dot"></div>
                  <div className="timeline-content creative-timeline-content">
                    <h3>{edu.degree || "Degree"}</h3>
                    <p className="timeline-subtitle creative-timeline-subtitle">
                      {edu.institution || "Institution"} |{" "}
                      {edu.dates || "Dates"}
                    </p>
                    {edu.gpa && (
                      <p>
                        <strong>GPA:</strong> {edu.gpa}
                      </p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </section>
        )}
      </main>

      {/* == Sidebar Column == */}
      <aside className="resume-sidebar creative-resume-sidebar">
        {/* AI Analysis Section */}
        {resume_analysis && (
          <Card className="ai-analysis-card creative-ai-analysis-card">
            <h2>🤖 AI Analysis</h2>
            <div className="ai-rating creative-ai-rating">
              <span className="ai-rating-score creative-ai-rating-score">
                {resume_analysis.rating_out_of_10 || "N/A"}
              </span>
              <span className="ai-rating-label creative-ai-rating-label">
                / 10
              </span>
            </div>
            <p className="ai-feedback creative-ai-feedback">
              {resume_analysis.general_feedback || "No feedback available."}
            </p>
            <div className="accordion-group creative-accordion-group">
              <Accordion
                title="✅ Strengths"
                items={resume_analysis.strengths}
              />
              <Accordion
                title="⚠️ Weaknesses"
                items={resume_analysis.weaknesses}
              />
              <Accordion
                title="🚀 Improvements"
                items={resume_analysis.areas_for_improvement}
              />
              <Accordion
                title="💡 Skill Suggestions"
                items={resume_analysis.suggested_upskilling_skills}
              />
            </div>
          </Card>
        )}

        {/* Skills Section */}
        {skills &&
          Object.keys(skills).some((key) => skills[key]?.length > 0) && (
            <Card>
              <h2>🧠 Skills</h2>
              {Object.entries(skills).map(
                ([category, skillsArray]) =>
                  skillsArray?.length > 0 && (
                    <div
                      key={category}
                      className="skill-category creative-skill-category"
                    >
                      <h4>{category.replace(/_/g, " ")}</h4>
                      <div className="badge-container creative-badge-container">
                        {skillsArray.map((skill) => (
                          <Badge key={skill} text={skill} />
                        ))}
                      </div>
                    </div>
                  )
              )}
            </Card>
          )}

        {/* Awards & Certifications */}
        {awards_certifications?.length > 0 && (
          <Card>
            <h2>🏆 Awards & Certs</h2>
            <ul>
              {awards_certifications.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </Card>
        )}
      </aside>
    </div>
  );
};

export default ResumeDetails;
