// frontend/src/components/ResumeUpload.js
import React, { useState } from "react";
import { uploadResume } from "../api";
import ResumeDetails from "./ResumeDetails";

const ScoreBar = ({ score }) => {
  // Clamp score between 0 and 10
  const percent = Math.max(0, Math.min(10, score || 0)) * 10;
  return (
    <div className="scorebar-outer">
      <div className="scorebar-label">ATS Resume Score</div>
      <div className="scorebar-circle">
        <svg width="100" height="100">
          <circle
            cx="50"
            cy="50"
            r="44"
            stroke="#e0e7ef"
            strokeWidth="8"
            fill="none"
          />
          <circle
            cx="50"
            cy="50"
            r="44"
            stroke="#21d4fd"
            strokeWidth="8"
            fill="none"
            strokeDasharray={276}
            strokeDashoffset={276 - (276 * percent) / 100}
            strokeLinecap="round"
            style={{ transition: "stroke-dashoffset 1s" }}
          />
        </svg>
        <div className="scorebar-value">
          <span>{score !== null && score !== undefined ? score : "--"}</span>
          <span className="scorebar-max">/10</span>
        </div>
      </div>
    </div>
  );
};

const ResumeUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setAnalysisResult(null);
    setError("");
    setSuccessMessage("");
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError("Please select a resume PDF file to upload.");
      return;
    }
    setLoading(true);
    setError("");
    setSuccessMessage("");
    setAnalysisResult(null);
    try {
      const response = await uploadResume(selectedFile);
      setAnalysisResult(response.data.data.extracted_data);
      setSuccessMessage(response.data.message);
    } catch (err) {
      console.error("Error uploading resume:", err);
      const errorMessage =
        err.response?.data?.message ||
        "Failed to upload and analyze resume. Please try again.";
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="ats-hero-bg">
      <div className="ats-hero">
        <h1 className="ats-hero-title">Free ATS Resume Checker</h1>
        <p className="ats-hero-sub">
          Upload your resume and get instant, AI-powered feedback. See your ATS
          score and get actionable tips to improve your chances of getting
          hired!
        </p>
      </div>
      <div className="ats-upload-card">
        <div className="ats-upload-area">
          <label htmlFor="resume-upload" className="ats-upload-label">
            <div className="ats-upload-icon">📄</div>
            <div className="ats-upload-text">
              {selectedFile
                ? selectedFile.name
                : "Drag & drop or click to upload your PDF resume"}
            </div>
            <input
              id="resume-upload"
              type="file"
              accept=".pdf"
              onChange={handleFileChange}
              className="ats-upload-input"
              disabled={loading}
            />
          </label>
          <button
            onClick={handleUpload}
            className="ats-upload-btn"
            disabled={loading}
          >
            {loading ? (
              <>
                Analyzing... <span className="loading-spinner"></span>
              </>
            ) : (
              "Check My Resume"
            )}
          </button>
        </div>
        {error && <div className="ats-error-message">{error}</div>}
        {successMessage && (
          <div className="ats-success-message">{successMessage}</div>
        )}
      </div>
      {analysisResult && (
        <div className="ats-results-section">
          <div className="ats-scorebar-wrap">
            <ScoreBar
              score={analysisResult.resume_analysis?.rating_out_of_10}
            />
          </div>
          <div className="ats-feedback-cards">
            <div className="ats-feedback-card">
              <h3>AI Feedback</h3>
              <p>
                {analysisResult.resume_analysis?.general_feedback ||
                  "No feedback available."}
              </p>
            </div>
            <div className="ats-feedback-card">
              <h3>Strengths</h3>
              <ul>
                {(analysisResult.resume_analysis?.strengths || []).map(
                  (item, i) => (
                    <li key={i}>{item}</li>
                  )
                )}
              </ul>
            </div>
            <div className="ats-feedback-card">
              <h3>Areas for Improvement</h3>
              <ul>
                {(
                  analysisResult.resume_analysis?.areas_for_improvement || []
                ).map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </div>
            <div className="ats-feedback-card">
              <h3>Suggested Skills</h3>
              <ul>
                {(
                  analysisResult.resume_analysis?.suggested_upskilling_skills ||
                  []
                ).map((item, i) => (
                  <li key={i}>{item}</li>
                ))}
              </ul>
            </div>
          </div>
          <div className="ats-full-details">
            <ResumeDetails data={analysisResult} />
          </div>
        </div>
      )}
    </div>
  );
};

export default ResumeUpload;
