import React, { useEffect, useRef } from "react";
import "./LandingPage.css";

const LandingPage = ({ onGetStarted }) => {
  const bgImgRef = useRef();
  const paperRef = useRef();

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      // Rotate up to 30deg as you scroll 0-500px
      const rotate = Math.min(30, scrollY / 16);
      if (paperRef.current) {
        paperRef.current.style.transform = `perspective(1200px) rotateY(${rotate}deg)`;
      }
      // Rotate background image (slower for parallax effect)
      if (bgImgRef.current) {
        bgImgRef.current.style.transform = `rotate(${rotate * 0.7}deg)`;
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="landing-bg">
      {/* Rotating background image */}
      <div className="landing-bg-img" ref={bgImgRef} />
      <div className="landing-3d-wrapper">
        <div className="landing-3d-paper" ref={paperRef}>
          <h1 className="landing-title">Welcome to Resume Analyzer</h1>
          <p className="landing-sub">
            AI-powered, ATS-friendly, and beautifully designed. Analyze your
            resume and get hired faster!
          </p>
          <button className="landing-cta" onClick={onGetStarted}>
            Get Started
          </button>
        </div>
      </div>
      {/* Dark theme sections in a row */}
      <div className="landing-sections-row">
        <section className="landing-section dark-section">
          <div className="section-icon">‚ú®</div>
          <h2 className="section-title">Features</h2>
          <p className="section-desc">
            Instant ATS analysis, AI-powered feedback, beautiful reports, and
            more. Upload your resume and get actionable insights in seconds.
          </p>
        </section>
        <section className="landing-section dark-section">
          <div className="section-icon">‚öôÔ∏è</div>
          <h2 className="section-title">How It Works</h2>
          <p className="section-desc">
            1. Upload your resume. 2. Our AI analyzes it for ATS compliance and
            best practices. 3. Get a detailed, easy-to-read report and tips for
            improvement.
          </p>
        </section>
        <section className="landing-section dark-section">
          <div className="section-icon">üåô</div>
          <h2 className="section-title">Why Choose Us?</h2>
          <p className="section-desc">
            Modern design, 3D animations, and a focus on real results. Our tool
            is trusted by job seekers and built for the future of hiring.
          </p>
        </section>
      </div>
      <div className="landing-scroll-indicator">
        <span className="scroll-arrow">‚Üì</span>
        <span className="scroll-text">Scroll Down</span>
      </div>
    </div>
  );
};

export default LandingPage;
